var url      = require("url");
var os       = require("os");
var fs       = require("fs");


/*
 * Common Method Signature is function <Function Name> (request, response, query, hash) {...}
 * All methods should end() the responses, it is the programmer's responsibility
*/
function RootViewController (request, response, query, hash) {
	response.writeHead(200, {"Content-Type": "text/html"});
	response.write("<html><head></head><body><script src='main.js'></script><h1>You're at the root view</h1></body></html>");

	response.end();
}


function UserViewController (request, response, query, hash) {
	response.writeHead(200, {"Content-Type": "application/json"});
	var str = "{\n"
		+	"'you'     : 'are',\n"
		+	"'viewing' : 'the',\n"
		+	"'json'    : 'response',\n"
		+	"'and'     : 'your',\n"
		+	"'query'   : 'was',\n"
		+ JSON.stringify(query)
	+ "\n}";
	response.write(str);
	response.end();
}

function FaviconViewController (request, response, query, hash) {
	response.writeHead(200, {"Content-Type": "image/png"});
	response.end();
}



module.exports = {
	RootViewController    : RootViewController,
	UserViewController    : UserViewController,
  FaviconViewController : FaviconViewController
}