//All of my controller Functions/Methods
//There is an issue with hoisiting
var SFC = require('SFControllers');

var SFRoutes = {
	"/"     : SFC.RootViewController,
	"/user" : SFC.UserViewController,
	"/favicon.ico" : SFC.FaviconViewController
};




function RootViewController (response, query, hash){
	response.writeHead(200, {"Content-Type": "text/html"});
	response.write("<html><head></head><body><script src='main.js'></script><h1>You're at the root view</h1></body></html>");

	response.end();
}


function UserViewController (response, query, hash){
	response.writeHead(200, {"Content-Type": "application/json"});
	var str = "{\n"
		+	"'you'     : 'are',\n"
		+	"'viewing' : 'the',\n"
		+	"'json'    : 'response',\n"
		+	"'and'     : 'your',\n"
		+	"'query'   : 'was',\n"
		+ JSON.stringify(query)
	+ "\n}";
	response.write(str);
	response.end();
}

function FaviconViewController (response){
	response.writeHead(200, {"Content-Type": "image/png"});
	response.end();
}

module.exports = SFRoutes;	